	public static function checkServerSettings($return_error_names = false)
	{
		$error_name = array();
		$error = 0;
		
		// Check tmp folder is writeable
		$tmp_path = JPATH_ROOT.'/tmp/';
		
        if (strtoupper(substr(PHP_OS, 0, 3)) !== 'WIN')
        {
    		if (!is_writable($tmp_path))
    		{
    			chmod ( $tmp_path , 0777 ); 
    			if (!is_writable($tmp_path))
    			{
    				$error = 1;
    				$error_name[] = 'tmp folder not writeable. '.$tmp_path;
    			}
    		}
        }
		
		/*
		// Check ssh
		if ( !function_exists('exec') ) 
		{
		    if (!class_exists('ZipArchive'))
		    {
				$error = 1;
				$error_name[] = 'PHP function exec is disabled, class ZipArchive is not installed';
			}
		}
		
		// Check CURL
		if ( !function_exists('curl_init') ) 
		{
			$error = 1;
			$error_name[] = 'CURL is not enabled';
		}
		*/
		
		if ($return_error_names) return json_encode($error_name);
		if ($error == 1) return false;
		else return true;
	}




	public static function ShowFilesForAnalyze($files_array = array())
	{
		$files = array();
		
		if (count($files_array['main']))
		{
			foreach ($files_array['main'] as $k => $filename)
			{
				$files[$filename] = $filename;	
			}
		}
		if (count($files_array['heuristic']))
		{
			foreach ($files_array['heuristic'] as $k => $filename)
			{
				$files[$filename] = $filename;	
			}
		}
		
		sort($files);
		
		echo '<pre>';
		print_r($files);
		echo '</pre>';
		
		echo '<br><br>';
		
		if (count($files))
		{
			foreach ($files as $file)
			{
				$file_full_path = JPATH_SITE.'/'.$file;
				echo $file_full_path.' Filesize: '.filesize($file_full_path).' bytes<br><br>';
				$handle = fopen($file_full_path, "r");
				$content =  fread($handle, filesize($file_full_path));
				echo 'Content: <pre>'.$content.'</pre>';
				fclose($handle);
				
				echo '<br><br><hr><br><br>';
			}
		}
		
		
	}
	
	

	public static function SendFilesForAnalyze($files_array = array(), $email_from)
	{
		if (strtoupper(substr(PHP_OS, 0, 3)) === 'WIN') define(DIRSEP, '\\');
		else define(DIRSEP, '/');
		
		$result = false;
		$files = array();
		
		if (count($files_array['main']))
		{
			foreach ($files_array['main'] as $k => $filename)
			{
				$files[$filename] = $filename;	
			}
		}
		if (count($files_array['heuristic']))
		{
			foreach ($files_array['heuristic'] as $k => $filename)
			{
				$files[$filename] = $filename;	
			}
		}
		sort($files);
		
		
		if (count($files))
		{
			$md5_list = array();
			
			$attachments = $files;
			$message_files = '';
			foreach ($attachments as $k => $v)
			{
				$attachments[$k] = JPATH_ROOT.DIRSEP.$v;
				$message_files .= $v."\n";
				$md5_list[] = array(
					'File' => $v,
					'md5' => strtoupper(md5_file(JPATH_ROOT.'/'.$v))
				);
			}
			
			
			
			$separator = md5(time());
			$eol = PHP_EOL;
			
			// main header (multipart mandatory)
			$headers = "From: ".JURI::root()." <".$email_from.">" . $eol;
			$headers .= "MIME-Version: 1.0" . $eol;
			$headers .= "Content-Type: multipart/mixed; boundary=\"" . $separator . "\"" . $eol . $eol;
			$headers .= "Content-Transfer-Encoding: 7bit" . $eol;
			$headers .= "This is a MIME encoded message." . $eol . $eol;
			
			// message
			$message = 'Files for review. Domain: '.JURI::root()."\n\n".$message_files."\n\n".print_r($md5_list, true);
			$headers .= "--" . $separator . $eol;
			$headers .= "Content-Type: text/plain; charset=\"iso-8859-1\"" . $eol;
			$headers .= "Content-Transfer-Encoding: 8bit" . $eol . $eol;
			$headers .= $message . $eol . $eol;
			
			
			// attachment
			foreach ($files as $file)
			{
				$filename = basename($file);
				$file_full_path = JPATH_SITE.'/'.$file;
				$file_size = filesize($file_full_path);
				$handle = fopen($file_full_path, "r");
				$content = fread($handle, $file_size);
				fclose($handle);
				$content = chunk_split(base64_encode($content));
				
				$headers .= "--" . $separator . $eol;
				$headers .= "Content-Type: application/octet-stream; name=\"" . $filename . "\"" . $eol;
				$headers .= 'Content-Description: ' . $file. $eol;
				$headers .= "Content-Transfer-Encoding: base64" . $eol;
				$headers .= 'Content-Disposition: attachment filename="' . $filename . '"; size=' . $file_size.  ';' . $eol . $eol;
				$headers .= $content . $eol . $eol;
			}
			
			$headers .= "--" . $separator . "--". $eol;
			
			//Send Mail
			$subject = 'Antivirus Files Review ('.JURI::root().')';
			$mailto = 'review@siteguarding.com';
			$result = mail($mailto, $subject, "", $headers);
		}
		
		
		return $result;
	}
	
	

	public static function QuarantineFiles($files = array())
	{
		$tmp_path = JPATH_ROOT.'/tmp/';
		
		$fp = fopen($tmp_path.'/quarantine.log', 'a');
		
		$result = true;
		
		$quarantine_path = $tmp_path.'/';
		
		//print_r($files);
		if (count($files))
		{
			foreach ($files as $file)
			{
				if (file_exists(JPATH_SITE.'/'.$file))
				{
					$f_from = JPATH_SITE.'/'.$file;
					$f_to = $quarantine_path.md5($file).'.tmp';
					
					$a = date("Y-m-d H:i:s")." File ".$file."\n";
					fwrite($fp, $a);
					
					// Move to quarantine
					if (copy($f_from, $f_to) === false) 
					{
						$result = false;
						
						$a = date("Y-m-d H:i:s")." File is not moved to quarantine ".$file."\n";
						fwrite($fp, $a);
					}
					else {
						$a = date("Y-m-d H:i:s")." Moved to quarantine as ".$f_to."\n";
						fwrite($fp, $a);
					}
					
					// Delete from the server
					if (unlink($f_from) === false)
					{
						$result = false;
						
						$a = date("Y-m-d H:i:s")." File is not deleted ".$file."\n";
						fwrite($fp, $a);
					}
					else {
						$a = date("Y-m-d H:i:s")." File deleted ".$file."\n";
						fwrite($fp, $a);
					}
				}
			}
		}
		
		fclose($fp);
		
		return $result;
	}
}


