	public static function getReport($domain, $access_key, $session_report_key)
	{
		include_once(dirname(__FILE__).'/HttpClient.class.php');
		$HTTPClient = new HTTPClient();
		
			$post_data = array(
				'data'=> base64_encode(json_encode(array(
					'domain' => $domain,
					'access_key' => $access_key,
					'session_report_key' => $session_report_key)))
			);
		
			$result_json = $HTTPClient->post(self::$SITEGUARDING_SERVER.'?action=getreport_ver2', $post_data);
			if ($result_json === false) 
			{
				$error_msg = 'Report can not be generated. Please try again or contact support';
				if (self::$debug) self::DebugLog($error_msg);
				echo $error_msg;
				exit;
			}
			$result_json = (array)json_decode($result_json, true);
			if ($result_json['status'] == 'ready') 
			{
				return $result_json['text'];
			}
			
			return false;
	}
	

	public static function AntivirusFinished()
	{
		if (self::$debug) self::DebugLog('PHP process has been terminated');
		
		$fp = fopen(dirname(__FILE__).DIRSEP.'tmp'.DIRSEP.'flag_terminated.tmp', 'w');
		$a = date("Y-m-d H:i:s")." Terminated";
		fwrite($fp, $a);
		fclose($fp);	
	}
	

	public static function scan($check_session = false, $show_results = true)
	{
	    if (strtoupper(substr(PHP_OS, 0, 3)) === 'WIN') define(DIRSEP, '\\');
		else define(DIRSEP, '/');
        
        // Load scanner class
		if (file_exists(dirname(__FILE__).DIRSEP.'scanner.class.php'))
		{
		  
			require_once(dirname(__FILE__).DIRSEP.'scanner.class.php');	
            
            if (defined('JPATH_ROOT')) $scan_path = JPATH_ROOT;
            else $scan_path = trim($_POST['scan_path']);
            $scan_path = $scan_path.DIRSEP;
            $scan_path = str_replace(DIRSEP.DIRSEP, DIRSEP, $scan_path);
            
            $access_key = trim($_POST['access_key']);
            $domain = trim($_POST['domain']);
			$licenseinfo = SGAntiVirus::GetLicenseInfo($domain, $access_key);
            
            $scanner = new SGAntiVirus_scanner();
            
            $scanner->antivirus_version = SGAntiVirus::$antivirus_version;
            $scanner->antivirus_platform = SGAntiVirus::$antivirus_platform;
            $scanner->antivirus_cms = SGAntiVirus::$antivirus_cms;
            
            $scanner->work_dir = dirname(__FILE__).DIRSEP;
            $scanner->tmp_dir = $scan_path.'tmp'.DIRSEP;
            $scanner->membership = $licenseinfo['membership'];
            $scanner->scan_path = $scan_path;
            $scanner->access_key = $access_key;
            $scanner->domain = $domain;
            $scanner->email = trim($_POST['email']);
            $scanner->session_report_key = trim($_POST['session_report_key']);  
            
            
            $scanner->scanner();
            
        }
        else {
            die('scanner.class.php is not loaded');
        }

		exit;
	}



	
	
	public static function readProgress()
	{
		error_reporting(0);
		
		//$tmp_path = $_SERVER['DOCUMENT_ROOT'].'/tmp/';
		$tmp_path = JPATH_ROOT.'/tmp/';
		
		
		$a = array('txt' => 'Loading...', 'progress' => 0);
		
		// Read log file
		$filename = $tmp_path."antivirus_last_action.log";
		$handle = fopen($filename, "r");
		if ($handle === false) return $a['progress']."|".'Progress bar is disabled.';
		$contents = fread($handle, filesize($filename));
		fclose($handle);
		
		$contents = json_decode($contents, true);
		if ($contents == NULL || $contents === false) return $a['progress']."|".'Progress bar is disabled [2]';
		
		$a['txt'] = trim($contents['txt']);
		$a['progress'] = floatval($contents['progress']);
		
		$val = $a['progress'];
		$new_val = round($val+0.1 , 2);
		if ($new_val > 100) $new_val = 90;
		$val_txt = trim($a['txt']);
		
		$a = array(
			'txt' => $val_txt,
			'progress' => $new_val
		);
		
	
		$filename = $tmp_path."antivirus_last_action.log";
		$fp = fopen($filename, 'w');
		fwrite($fp, json_encode($a));
		fclose($fp);
		
		return $val."|".$val_txt;
	}
	
	




