	public static function DebugLog($txt, $clean_log_file = false)
	{
		//$mainframe = JFactory::getApplication();
		//$tmp_dir = $mainframe->getCfg('tmp_path');
		$tmp_path = JPATH_ROOT.'/tmp/';
		
		if ($clean_log_file) $fp = fopen($tmp_dir.'/antivirus_debug.log', 'w');
